        };
        InitializeDb();
    }

    public async Task InitializeDb()
    {
        LocalDatabaseService _localDatabaseService = new LocalDatabaseService();
        await _localDatabaseService.BulkCreateRecipes(RecipeList);
    }



    public RecipeModel Initialize(int id, string image, string title, TimeSpan prepTime, TimeSpan cookTime, string description,
        string ingredients, string num_ingredients, string steps, bool isFave, bool isCustom, string category)
    {
        RecipeModel recipe = new RecipeModel();
        recipe.Id = id;
        recipe.Image = image;
        recipe.Title = title;
        recipe.PreparationTime = prepTime;
        recipe.CookingTime = cookTime;
        recipe.Description = description;
        recipe.Ingredients = ingredients;
        recipe.NumberOfIngredients = num_ingredients;
        recipe.Steps = steps;
        recipe.IsFave = isFave;
        recipe.IsCustom = isCustom;
        recipe.Category = category;
        return recipe;
    }
}
